var SerialPortStream = require('../browser.js');

var buff = new Buffer([0x02, 0x03, 0x06, 0x30, 0x41, 0xb3]);

chrome.app.runtime.onLaunched.addListener(function() {

  var stream = new SerialPortStream('/dev/tty.usbserial-A100dgz9', { baudRate : 9600 });

  stream.on('open', function () {
    console.log('open');
  });

  stream.write(buff);

});
